package controller;

import model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;

@WebServlet(name = "CustomerServlet", urlPatterns = "/customer")
public class CustomerServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        Customer[] customers=new Customer[3];
        customers[0]=new Customer("Mai Văn Hoàn", "1998-08-12", "Hà Nội","data:image/jpeg;base64," );
        customers[1]=new Customer("Nguyễn Văn Nam", "1998-0-12", "Hà Nội", "data:image/jpeg;base64,");
        customers[2]=new Customer("Lê Bảo", "2008-10-19", "Hà Nội", "data:image/jpeg;base64,");
        request.setAttribute("listCustomer", customers);
        request.getRequestDispatcher("list.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
